namespace Toystore.AspNetCore.Test.Products
{
    public class ProductsControllerTest
    {

        [Fact]
        public async Task FindProducts_ReturnsThreeRows()
        {
            var configuration = new ProductContextConfiguration { DatabaseName = Guid.NewGuid().ToString() };
            var productContext = new ProductContext(configuration);
            var productRepository = new ProductRepository(productContext);
            var productService = new ProductService(productRepository);
            var productsController = new ProductsController(productService);

            var products = new List<Product>
                {
                    new Product { Id = 0, Name = "Max Steel", Description = "Action Figure", MinimumAge = 0, Company = "Mattel", Price = (decimal) 150.00, Image = null },
                    new Product { Id = 0, Name = "Jessie", Description = "Disney Collection Figure", MinimumAge = 0, Company = "Disney Collection", Price = (decimal) 524.30, Image = null },
                    new Product { Id = 0, Name = "Woody", Description = "Disney Collection Figure", MinimumAge = 0, Company = "Disney Collection", Price = (decimal) 599.20, Image = null }
                };

            await productContext.AddRangeAsync(products);
            await productContext.SaveChangesAsync();

            var productResponse = await productsController.FindProducts();

            Assert.True(productResponse.Success);
            Assert.Equal(3, productResponse.Obj.Count);
        }

