        [Fact]
        public async Task SaveProduct_ReturnsProductAlreadyExists()
        {
            var configuration = new ProductContextConfiguration { DatabaseName = Guid.NewGuid().ToString() };
            var productContext = new ProductContext(configuration);
            var productRepository = new ProductRepository(productContext);
            var productService = new ProductService(productRepository);
            var productsController = new ProductsController(productService);

            var product = new Product
            {
                Id = 0,
                Name = "Captain America",
                Description = "Captain America Action Figure",
                MinimumAge = 5,
                Company = "Hasbro",
                Price = (decimal)289.00,
                Image = ""
            };

            await productContext.AddAsync(product);
            await productContext.SaveChangesAsync();

            var anotherProduct = new ProductView
            {
                Id = 0,
                Name = "Captain America",
                Description = "Captain America Action Figure",
                MinimumAge = 5,
                Company = "Hasbro",
                Price = (decimal)289.00,
                Image = ""
            };

            var productResponse = await productsController.SaveProduct(anotherProduct);

            Assert.False(productResponse.Success);
            Assert.Equal($"Server Error: There is already a product called { anotherProduct.Name }", productResponse.Message);
        }

        [Fact]
        public async Task SaveProduct_ReturnsProduct()
        {
            var configuration = new ProductContextConfiguration { DatabaseName = Guid.NewGuid().ToString() };
            var productContext = new ProductContext(configuration);
            var productRepository = new ProductRepository(productContext);
            var productService = new ProductService(productRepository);
            var productsController = new ProductsController(productService);

            var product = new ProductView
            {
