        }

        [Fact]
        public async Task UpdateProduct_ProductDoesNotExist()
        {
            var configuration = new ProductContextConfiguration { DatabaseName = Guid.NewGuid().ToString() };
            var productContext = new ProductContext(configuration);
            var productRepository = new ProductRepository(productContext);
            var productService = new ProductService(productRepository);
            var productsController = new ProductsController(productService);

            var product = new Product
            {
                Id = 0,
                Name = "Captain America",
                Description = "Captain America Action Figure",
                MinimumAge = 5,
                Company = "Hasbro",
                Price = (decimal)289.00,
                Image = ""
            };

            await productContext.AddAsync(product);
            await productContext.SaveChangesAsync();

            var sameProduct = new ProductView
            {
                Id = 25,
                Name = "Captain America",
                Description = "Captain America Action Figure",
                MinimumAge = 10,
                Company = "Hasbro",
                Price = (decimal)289.00,
                Image = ""
            };

            var productResponse = await productsController.UpdateProduct(sameProduct);

            Assert.False(productResponse.Success);
            Assert.Equal("Server Error: Product doesn't exist.", productResponse.Message);
        }

        [Fact]
        public async Task UpdateProduct_AlreadyExistsAnotherProduct()
        {
            var configuration = new ProductContextConfiguration { DatabaseName = Guid.NewGuid().ToString() };
            var productContext = new ProductContext(configuration);
            var productRepository = new ProductRepository(productContext);
            var productService = new ProductService(productRepository);
            var productsController = new ProductsController(productService);

            var product1 = new Product { Id = 0, Name = "Max Steel", Description = "Action Figure", MinimumAge = 0, Company = "Mattel", Price = (decimal)150.00, Image = null };
            
            await productContext.AddAsync(product1);
            await productContext.SaveChangesAsync();

            var product2 = new Product { Id = 0, Name = "Jessie", Description = "Disney Collection Figure", MinimumAge = 0, Company = "Disney Collection", Price = (decimal)524.30, Image = null };
            await productContext.AddAsync(product2);
            await productContext.SaveChangesAsync();

            var product = new ProductView
            {
                Id = product2.Id,
                Name = "Max Steel",
                Description = "Disney Collection Figure",
                MinimumAge = 0,
                Company = "Disney Collection",
                Price = (decimal)524.30,
                Image = null
            };

            var productResponse = await productsController.UpdateProduct(product);

            Assert.False(productResponse.Success);
            Assert.Equal($"Server Error: There is already another product called { product.Name }.", productResponse.Message);
        }

